mapper.update:
UPDATE animal_comment
SET updated_at = NOW() , content = #{content} , user_id = #{user_id}
WHERE  id = #{id}

UPDATE animal_comment
SET is_deleted = 1
WHERE id = #{id}

mapper.select:
SELECT * FROM whopuppy.animal_comment
WHERE is_deleted = 0 AND article_id = #{article_id}
ORDER BY id DESC, created_at DESC
LIMIT #{cursor},#{limit}
